import { useState } from 'react'
import Person from './components/Person'

const App = () => {
  const [persons, setPersons] = useState([
    { content: 'Arto Hellas',
    number: '040-1234567',
    id: 1 }
  ]) 

  const [newPerson, setNewPerson] = useState('')

  const addPerson = (event) => {
    event.preventDefault()
    const nameObject = {
      content: newPerson.content,
      number: newPerson.number
    }

    let validPers = persons.some(el => el.content === nameObject.content)
    if(!validPers){
      console.log("el", validPers)
      setPersons(persons.concat(nameObject))
    } else {
      alert(`${nameObject.content} is already added to phonebook`)
    }
    setNewPerson('')
  }



  const handlePersonChange = (event) => {
    setNewPerson(event.target.value)
  }

  const handleNumberChange = (event) => {
    //setNewNumber(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit = {addPerson}>
        <div>
          name: <input value = {newPerson.content}
          onChange = {handlePersonChange}
          />
        </div>
        <div>
          number: <input value = {newPerson.number}
          onChange = {handlePersonChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons.map(person => 
        <Person key = {person.content} content = {person.content} number = {person.number} />)} 

    </div>
  )
}

export default App
